import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, NavLink } from "react-router-dom";
import styles from "./styles.module.css";
import Swal from 'sweetalert2';
// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
// import { fa-clock-o } from '@fortawesome/free-solid-svg-icons';
import Tasks from "../Tasks";
import { useDispatch } from "react-redux";

const TaskItems = (props) => {
    const [tasks, setTasks] = useState({
        result: []
    });

    const [error, setError] = useState("");

    const viewTasks = async () => {
        try {
            const url = "http://localhost:8080/api/projects/tasks/";
            await axios.get(url, { params: { projectId: props.details.projectId } }).then((response) => {
                const arr = response.data;
                console.log(arr);
                setTasks((prevTasks) => {
                    return {
                        ...prevTasks,
                        result: arr,
                    }
                });
            }
            );
        } catch (error) {
            console.log(error.response.statusText);
        }
    };

    useEffect(() => {
        viewTasks();
    }, []);

    if(tasks.result.length === 0) {
        setError("No tasks have been assigned!");
    }

    return (
        <div>
            <h2>{props.projectTitle}</h2>
            {!error && <table>
                <tr>
                    <th>Task</th>
                    <th>Description</th>
                    <th>Assigned user</th>
                    <th>Status</th>
                </tr>
                {tasks.result.map((item) => {
                    return (
                        <tr key={item._id}>
                            <td>{item.title}</td>
                            <td>{item.description}</td>
                            <td>{item.assignedUserEmail}</td>
                            <td>{item.status}</td>
                        </tr>
                    )
                })}
            </table>}
        </div>
    )
}

export default TaskItems;
