import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import styles from "./styles.module.css";

const Tasks = () => {
    const user = localStorage.getItem("token");
    const date = new Date();
    const [data, setData] = useState({
        user: user,
        result: []
    });
    

    useEffect(() => {
        const url = "http://localhost:8080/api/projects/";
            axios.get(url, { params: { token: data.user } }).then((response) => {
                const arr = response.data;
                console.log(arr);
                arr.map((item) => {
                    console.log("i'm inside");
                    data.result.push(
                        {title: item.title,
                                description: item.description,
                                date: item.date,
                                status: item.status,
                                id: item._id}
                    )
                });

                console.log(data);
            }
            );
    });

    return (
        <section style={{
            display: 'flex',
            gridGap: '20px',
            justifyContent: 'center',
            alignItems: 'center',
            // gridTemplateColumns: 'repeat(3, calc(33.33% - 20px))',
            flexWrap: 'wrap',
            marginTop: '100px',
        }}>
            {data.result.map((item) => {
                return (
                    <div key={item.id} style={{
                        width: '300px',
                        padding: '50px 50px 50px 50px',
                        margin: 'auto',
                        borderRadius: '10px',
                        textAlign: 'center',
                        marginBottom: '50px',
                        boxShadow: '0px 0px 20px 0px rgba(0,0,0,0.4)',
                        overflowWrap: 'break-word'
                    }}>
                        <h2>{item.title}</h2><br />
                        <address>{item.date}</address><br />
                        <p style={{ fontSize: '18px' }}>{item.description}</p><br /><br />
                        <address style={{ color: 'green' }}>
                            {/* <FontAwesomeIcon icon={fa fa-clock-o} />&nbsp;&nbsp; */}
                        {item.status}</address>
                        
                    </div>
                )
            })}
        </section>
    );
}

export default Tasks;
