import { createStore } from "redux";

const tasksReducer = (state = { id: "", title: "", description: "", status: "", date: "" }, action) => {
    if (action.type === 'setProjectId') {
        return {
            id: action.projectId
        }
    }
    if (action.type === 'setProjectTitle') {
        return {
            title: action.projectTitle
        }
    }
    if (action.type === 'setProjectDescription') {
        return {
            description: action.projectDescription
        }
    }
    if (action.type === 'setProjectStatus') {
        return {
            status: action.projectStatus,
        }
    }
    if (action.type === 'setProjectDate') {
        return {
            date: action.projectDate
        }
    }
    return state;
};

const store = createStore(tasksReducer);

export default store;
