import React from "react";
import { Chart } from "react-google-charts";
import styles from "./styles.module.css";

const Home = (props) => {
    const pending = props.todoDetails.filter(item => item.status == "pending");
    const completed = props.todoDetails.filter(item => item.status == "completed");

    const data = [
        ["Task", "Hours per Day"],
        ["Pending", pending.length],
        ["Completed", completed.length] // CSS-style declaration
    ];

    const options = {
        title: "Todo Tasks",
        pieHole: 0.3,
        is3D: false,
    };

    return (
        <div>
            <h1>Home</h1><br />
            {(pending.length || completed.length) === 0 && <h2>You haven't been asigned for any task!</h2>}
            {(pending.length || completed.length) != 0 && <section>
                <div>
                    <section className={styles.cards}>
                        <div className={styles.card_items}>
                            <h1>{pending.length}</h1><br />
                            <address style={{ color: 'green' }}>
                                Pending Tasks
                            </address>
                        </div>
                        <div className={styles.card_items}>
                            <Chart
                                chartType="PieChart"
                                width="600px"
                                height="600px"
                                data={data}
                                options={options}
                            />
                        </div>
                        <div className={styles.card_items}>
                            <h1>{completed.length}</h1><br />
                            <address style={{ color: 'green' }}>
                                Completed Tasks
                            </address>
                        </div>
                    </section>
                </div>
            </section>}
        </div>
    );
}

export default Home;
