import { createStore } from "redux";

const tasksReducer = (state = { id: "", title: "", description: "", status: "", date: "", taskId: "" }, action) => {
    if (action.type === 'setProjectDetails') {
        return {
            id: action.payload.projectId,
            title: action.payload.projectTitle
        }
    }
    
    return state;
};

const todoReducer = (state = { id: "", title: "", description: "", status: "", date: "", taskId: "" }, action) => {
    if (action.type === 'setTodo') {
        return {
            ...state,
            id: action.payload.result.projectId,
            title: action.payload.result.title,
            description: action.payload.result.description,
            status: action.payload.result.status,
            date: action.payload.result.date,
            taskId: action.payload.result._id
        }
    }
    
    return state;
};

const store = createStore({tasksReducer, todoReducer});

export default store;
