import React from "react";
import { Chart } from "react-google-charts";
import styles from "./styles.module.css";

const Home = (props) => {
    const pending = props.todoDetails.filter(item => item.status == "pending");
    const completed = props.todoDetails.filter(item => item.status == "completed");

    const data = [
        ["Task", "Hours per Day"],
        ["Pending", pending.length],
        ["Completed", completed.length] // CSS-style declaration
    ];

    const options = {
        title: "Todo Tasks",
        pieHole: 0.3,
        is3D: false,
    };

    return (
        <div>
            <h1>Home</h1><br />
            {(pending.length || completed.length) === 0 && <h2>You haven't been asigned for any task!</h2>}
            {(pending.length || completed.length) != 0 && <section>
                <div>
                    <section style={{
                        display: 'flex',
                        gridGap: '20px',
                        justifyContent: 'center',
                        alignItems: 'center',
                        // gridTemplateColumns: 'repeat(3, calc(33.33% - 20px))',
                        flexWrap: 'wrap',
                        marginTop: '100px',
                    }}>
                        <div style={{
                            width: '300px',
                            padding: '50px 50px 50px 50px',
                            margin: 'auto',
                            borderRadius: '10px',
                            textAlign: 'center',
                            marginBottom: '50px',
                            boxShadow: '0px 0px 20px 0px rgba(0,0,0,0.4)',
                            overflowWrap: 'break-word'
                        }}>
                            <h2>{pending.length}</h2><br />
                            <address style={{ color: 'green' }}>
                                Pending Tasks
                            </address>
                        </div>
                    </section>
                </div>
                <Chart
                    chartType="PieChart"
                    width="600px"
                    height="600px"
                    data={data}
                    options={options}
                />
            </section>}
        </div>
    );
}

export default Home;
