import React from "react";
import { Chart } from "react-google-charts";

const Home = (props) => {
    const data = [
        ["Task", "Hours per Day"],
        ["Pending", 11],
        ["Completed", 2] // CSS-style declaration
    ];

    const options = {
        title: "Todo Tasks",
        pieHole: 0.3,
        is3D: false,
    };

    const todo = props.todoDetails.map((item) => {
        status: item.status;
    });

    const pending = count(todo.filter(todo => todo.status.includes('pending')));
    console.log(pending);

    return (
        <div style={{display: 'flex'}}>
            <address>{props.todoDetails[1].title}</address>
            <h1>Home</h1>
            <section>
                <Chart
                    chartType="PieChart"
                    width="600px"
                    height="600px"
                    data={data}
                    options={options}
                />
            </section>
        </div>
    );
}

export default Home;
